craft.script:

 function main(){
    var cubes = []
    var r = 30, n = 30, theta = 0
    var delta = 2 * Math.PI / n
    for (var i = 0; i < n; i++){                
        var x = r * Math.cos(theta)
        var y = r * Math.sin(theta)
        cubes.push('')
        theta = theta + delta
    }                        
    return cubes.join('\n') 
}
